import React, { useState }from 'react'

const Header = ({heading}) => {
  return (
      <h1>{heading}</h1>
  )
}

const Button = ({clickHandle, name}) => {
  return (
    <>
      <button onClick={clickHandle}>{name}</button>
    </>
  )
}

const Data = ({name, variable}) => {
  return (
    <p>{name} : {variable}</p>
  )
}

const Average = ({good, bad}) => {
  return (
    <p>Average : {(good - bad)*100/3}</p>
  )
}





const App = () => {
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);


  return (
  <div>
    <Header heading="Give Feedback"/>
    <Button clickHandle={() => setGood(good + 1)} name={"Good"}/>
    <Button clickHandle={() => setNeutral(neutral + 1)} name={"Neutral"}/>
    <Button clickHandle={() => setBad(bad + 1)} name="Bad"/>
    <Header heading="Statistics"/>
    <Data name="Good" variable={good}/>
    <Data name="Neutral" variable={neutral}/>
    <Data name="Bad" variable={bad}/>
  </div>)
}

export default App;
